mapper.select:
SELECT ac.id, ac.customer_name AS name, ac.mobile AS mobile, ac.shop_name AS shopName, ac.sales_name AS salesName, ac.sales_no AS salesNo, ac.sales_mobile As salesMobile, date_format(ac.create_time,'%Y-%c-%d') As createTime, IF(alr.state IS NULL , '未录件' , '录件中') AS status FROM app_customer AS ac
LEFT JOIN app_lend_request alr ON ac.id = alr.app_customer_id
WHERE (alr.state = 1 OR alr.state = 2 OR alr.state is NULL )
AND ac.customer_name LIKE #{name}
AND ac.mobile LIKE #{mobile}
AND ac.sales_name LIKE #{salesName}
AND ac.sales_no LIKE #{salesNo}
AND ac.shop_code LIKE #{staffCode}
AND ac.sales_no in
#{sales}
ORDER BY ac.create_time DESC

mapper.update:
UPDATE app_customer ac SET ac.customer_service_manager_number = #{salesId}, ac.sales_name = #{name}, ac.sales_mobile = #{mobile}, ac.shop_name = #{shopName}, ac.sales_no = #{salesNo}, ac.shop_code = #{shopCode}, ac.district_code = #{districtCode}, ac.district_name = #{districtName}
where id in
#{id}
