mapper.update:
UPDATE app_weixin_article 
SET article = #{article},
author_name = #{authorName},
notice = #{notice},
article_status = #{articleStatus}
WHERE id = #{id}

mapper.select:
SELECT * FROM app_weixin_article
WHERE
    1=1

		AND author_name = #{authorName}


		AND article_status = #{noticeStatus}

ORDER BY create_time DESC
